import React, { useState, useEffect } from 'react';
import { loadStripe, Stripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { Streamlit, StreamlitComponentBase, withStreamlitConnection } from "streamlit-component-lib";

interface CheckoutFormProps {
  stripePublicKey: string;
}

const CheckoutForm: React.FC<CheckoutFormProps> = ({ stripePublicKey }) => {
  const [stripe, setStripe] = useState<Stripe | null>(null);
  const elements = useElements();
  const [message, setMessage] = useState("");

  useEffect(() => {
    if (stripePublicKey) {
      loadStripe(stripePublicKey).then(stripeInstance => {
        setStripe(stripeInstance);
      });
    }
  }, [stripePublicKey]);

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    if (!stripe || !elements) {
      console.log("Stripe.js aún no ha cargado.");
      return;
    }

    const card = elements.getElement(CardElement);
    if (card) {
      const result = await stripe.createToken(card);

      if (result.error) {
        console.log(result.error.message);
        setMessage(result.error.message);
        Streamlit.setComponentValue({ token: null, message: result.error.message });
      } else {
        console.log("Token generado: " + result.token.id);
        setMessage("Pago exitoso");
        Streamlit.setComponentValue({ token: result.token.id, message: "Pago exitoso" });
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <CardElement />
      <button type="submit" disabled={!stripe}>Pagar</button>
      <div>{message}</div>
    </form>
  );
};
